        <script>
            $(document).ready(function() {
                $(".main-carousel").flickity({
                    draggable: false,
                    freeScroll: false,
                    wrapAround: false,
                    groupCells: false,
                    autoPlay: false,
                    fullscreen: false,
                    fade: false,
                    adaptiveHeight: false,
                    setGallery: true,
                    initialIndex: 0,
                    accessibility: false,
                    cellAlign: 'center',
                    pageDots: false,
                    prevNextButtons: false,
                    percentPositions: true,
                    contain: true
                })
                $("#books-option").click(function(e) {handleBooksOptionClick(e)})
                $('#movies-option').click(function(e) {handleMoviesOptionClick(e)})
                $('#anime-option').click(function(e) {handleAnimeOptionClick(e)})
                $("#books-option").mouseenter(function(e) {handleBooksOptionHoverIn(e)})
                $("#movies-option").mouseenter(function(e) {handleMoviesOptionHoverIn(e)})
                $("#anime-option").mouseenter(function(e) {handleAnimeOptionHoverIn(e)})
                $("#books-option").mouseleave(function(e) {handleBooksOptionHoverOut(e)})
                $("#movies-option").mouseleave(function(e) {handleMoviesOptionHoverOut(e)})
                $("#anime-option").mouseleave(function(e) {handleAnimeOptionHoverOut(e)})
                $("#Add").click(function(e) {addGenre(e)})
                $(document).on('click', '.genre-delete', function (e) {deleteGenre(e)})
                $("#next-button").click(function(e) {handleNextClick(e)})
                $("#prev-button").click(function(e) {handlePrevClick(e)})
            })
        </script>
